% jacobian.m%function [lmat,umat,bscalinv] = jacobian(dtdx,gamma,rho,u,p,e)    m=length(rho);    lmat=zeros(3,3,m);    umat=zeros(3,3,m);    sprad=zeros(m);    beta = -1.0;    	speed = sqrt(gamma*p./rho);        % spectral radius at interface    for jj=1:m        sprad(jj) = (abs(u(jj))+speed(jj));    end	bscalinv(1) = 1.;    bscalinv(m) = 1.;    bscalinv(2:m-1)= 1./(1.-beta.*(dtdx*sprad(2:m-1)).^2);    for jj=2:m-1      lmat(1,1,jj)= beta*0.5*(dtdx*sprad(jj-1))^2;      lmat(2,2,jj)= beta*0.5*(dtdx*sprad(jj-1))^2;      lmat(3,3,jj)= beta*0.5*(dtdx*sprad(jj-1))^2;      umat(1,1,jj)= beta*0.5*(dtdx*sprad(jj+1))^2;      umat(2,2,jj)= beta*0.5*(dtdx*sprad(jj+1))^2;      umat(3,3,jj)= beta*0.5*(dtdx*sprad(jj+1))^2;    end    end